$("#settingBtn").click(function(){$("#setting").slideToggle("500")});$("#search").click(function(){var e=$("#term").val();if(e!=""){$("#response").html("");$.ajax({url:"/finder/search",data:{term:e,country:$("#c").val(),entity:$("#e").val()},dataType:"json",success:function(e){e.results>0?$("#response").html(e.content):$("#response").html("<p class='alert'>No results found.</p>")}})}});