$("#search").click(function(){var e=$("#upc").val();if(e=="")$("#response").html("").html('<div class="alert"><p>Please enter UPC to search.<p></div>');else{var t=/^\d*[0-9](|.\d*[0-9]|,\d*[0-9])?$/;if($.trim(e).length<12||t.test(e)==0)$("#response").html("").html('<div class="alert"><p>Invalid UPC.</p><p>Please enter a validate UPC.</p></div>');else{$("#response").html("");$.ajax({url:"/locator/search",data:{upc:e},dataType:"json",success:function(e){if(e.available>0){$("#response").html(e.content);$("#results").html(e.total)}else $("#response").html("<p class='alert'>No results found.</p>")}})}}});